mapper.insert:
insert into INPUT_HR_ORG (CODE, NAME, SPK_ORG, 
  ENABLESTATE, SHORTNAME, SCODE,
  SNAME, TS, DR, UPDATETIME, 
  UPDATETIMEBY, PK_ORG, RULE
  )
values (#{code,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{spkOrg,jdbcType=VARCHAR},
  #{enableState,jdbcType=DECIMAL}, #{shortName,jdbcType=VARCHAR}, #{sCode,jdbcType=VARCHAR},
  #{sName,jdbcType=VARCHAR}, #{ts,jdbcType=VARCHAR}, #{dr,jdbcType=DECIMAL}, #{updateTime,jdbcType=VARCHAR},
  #{updateTimeBy,jdbcType=VARCHAR}, #{pkOrg,jdbcType=VARCHAR}, #{rule,jdbcType=VARCHAR}
  )

mapper.select:
  select * from (  select rownum r,c.* from (SELECT CODE code,NAME name,SHORTNAME shortName,SCODE sCode,SNAME sName,'NC' orgType,enableState FROM ODS_HR_ORG
UNION ALL
SELECT CODE code,NAME name,SHORTNAME shortName,SCODE sCode,SNAME sName, '手工' orgType,enableState FROM INPUT_HR_ORG
ORDER BY CODE) c where 1=1

      and rownum <= #{pageIndex} * #{pageSize}

    )
   where  1=1

     and scode=#{sCode,jdbcType=VARCHAR}


  and code=#{code,jdbcType=VARCHAR}



      and  r >= (#{pageIndex}-1) * #{pageSize}
