mapper.insert:
insert into INPUT_HR_ORG (CODE, NAME, SPK_ORG, 
  ENABLESTATE, SHORTNAME, SCODE, 
  SNAME, TS, DR, UPDATETIME, 
  UPDATETIMEBY, PK_ORG, RULE
  )
values (#{CODE,jdbcType=VARCHAR}, #{NAME,jdbcType=VARCHAR}, #{SPK_ORG,jdbcType=VARCHAR}, 
  #{ENABLESTATE,jdbcType=DECIMAL}, #{SHORTNAME,jdbcType=VARCHAR}, #{SCODE,jdbcType=VARCHAR}, 
  #{SNAME,jdbcType=VARCHAR}, #{TS,jdbcType=VARCHAR}, #{DR,jdbcType=DECIMAL}, #{UPDATETIME,jdbcType=OTHER}, 
  #{UPDATETIMEBY,jdbcType=VARCHAR}, #{PK_ORG,jdbcType=VARCHAR}, #{RULE,jdbcType=VARCHAR}
  )

mapper.select:
select

from INPUT_HR_ORG


    and CODE = #{CODE}


    and NAME = #{NAME}


    and SPK_ORG = #{SPK_ORG}


    and ENABLESTATE = #{ENABLESTATE}


    and SHORTNAME = #{SHORTNAME}


    and SCODE = #{SCODE}


    and SNAME = #{SNAME}


    and TS = #{TS}


    and DR = #{DR}


    and UPDATETIME = #{UPDATETIME}


    and UPDATETIMEBY = #{UPDATETIMEBY}


    and PK_ORG = #{PK_ORG}


    and RULE = #{RULE}



  order by #{pageOrder}

limit #{pageStart}, #{pageRows}
