mapper.select:
select count(*) from
( select rownum r,c.* from (
select INPUT_HR_ZXB_CLIENT.code code,g.companycode companyCode,g.companyname
companyName,INPUT_HR_ZXB_CLIENT.Name name,INPUT_HR_ZXB_CLIENT.Company_Type company_type from INPUT_HR_ZXB_CLIENT
LEFT JOIN DIM_ORG G ON INPUT_HR_ZXB_CLIENT.CODE=G.SUBCOMPANYCODE
where 1=1

    and code = #{code,jdbcType=VARCHAR}


    and Name = #{name,jdbcType=VARCHAR}

) c

) where
1=1

select * from
   ( select rownum r,c.* from (
      select INPUT_HR_ZXB_CLIENT.code code,g.companycode companyCode,INPUT_HR_ZXB_CLIENT.Clientno clientNo,g.companyname companyName,INPUT_HR_ZXB_CLIENT.Name name,INPUT_HR_ZXB_CLIENT.Company_Type company_type    from INPUT_HR_ZXB_CLIENT
      LEFT JOIN DIM_ORG G ON INPUT_HR_ZXB_CLIENT.CODE=G.SUBCOMPANYCODE) c
      where 1=1

          and code = #{code,jdbcType=VARCHAR}


          and Name = #{name,jdbcType=VARCHAR}



          and rownum <= #{pageIndex} * #{pageSize}


    )  where
     1=1

          and  r >= (#{pageIndex}-1) * #{pageSize}
