mapper.update:
update INPUT_HR_ZXB_CLIENT
CLIENTNO = #{clientNo,jdbcType=VARCHAR}, UPDATEBY= #{updateBy,jdbcType=VARCHAR}, UPDATETIME=SYSDATE
where code = #{code,jdbcType=VARCHAR}

mapper.select:
select * from ( select rownum r,c.* from ( select aa.code,aa.COMPANYCODE companyCode,INPUT_HR_ZXB_CLIENT.Clientno clientNo,aa.companyname companyName,aa.Name name,aa.TAP company_type from ( SELECT CODE,G.COMPANYCODE,G.COMPANYNAME,NAME,'HR系统' AS TAP FROM ODS_HR_ORG O LEFT JOIN DIM_ORG G ON O.CODE=G.SUBCOMPANYCODE
UNION SELECT CODE, '' AS COMPANYCODE, '' AS COMPANYNAME, NAME, '手工填报' AS TAP FROM INPUT_HR_ORG ) aa left join INPUT_HR_ZXB_CLIENT on INPUT_HR_ZXB_CLIENT.Code=aa.code) c where 1=1
and c.code like '%'||#{code}||'%'
and c.name like '%'||#{name}||'%'
and rownum <= #{pageIndex} * #{pageSize}
) where 1=1
and r >= (#{pageIndex}-1) * #{pageSize}
order by company_type asc,code asc
