mapper.select:
select count(*) from (SELECT CODE,G.COMPANYCODE,G.COMPANYNAME,NAME,'HR系统' AS TAP FROM ODS_HR_ORG O LEFT JOIN DIM_ORG G ON O.CODE=G.SUBCOMPANYCODE
UNION SELECT CODE, '' AS COMPANYCODE, '' AS COMPANYNAME, NAME, '手工填报' AS TAP FROM INPUT_HR_ORG ORDER BY COMPANYCODE,CODE) aa left join INPUT_HR_ZXB_CLIENT on INPUT_HR_ZXB_CLIENT.Code=aa.code where 1=1
and aa.code = #{code,jdbcType=VARCHAR}
and aa.Name = #{name,jdbcType=VARCHAR}

select * from ( select rownum r,c.* from ( select aa.code,aa.COMPANYCODE companyCode,INPUT_HR_ZXB_CLIENT.Clientno clientNo,aa.companyname companyName,aa.Name name,aa.TAP company_type from (SELECT CODE,G.COMPANYCODE,G.COMPANYNAME,NAME,'HR系统' AS TAP FROM ODS_HR_ORG O LEFT JOIN DIM_ORG G ON O.CODE=G.SUBCOMPANYCODE
UNION SELECT CODE, '' AS COMPANYCODE, '' AS COMPANYNAME, NAME, '手工填报' AS TAP FROM INPUT_HR_ORG ORDER BY COMPANYCODE,CODE) aa left join INPUT_HR_ZXB_CLIENT on INPUT_HR_ZXB_CLIENT.Code=aa.code) c where 1=1
and c.code = #{code,jdbcType=VARCHAR}
and c.Name = #{name,jdbcType=VARCHAR}
and rownum <= #{pageIndex} * #{pageSize}
) where 1=1
and r >= (#{pageIndex}-1) * #{pageSize}
